data.etcd.sh:
#!/bin/bash
echo "staring etcd."
etcd_port=${ETCD_PORT:-'2379'}
etcd_server=${ETCD_SERVER:-'127.0.0.1'}

cell=${CELL:-'zone1'}
export ETCDCTL_API=2

etcd --enable-v2=true --data-dir "${VTDATAROOT}/etcd/"  \
  --listen-client-urls "http://0.0.0.0:${etcd_port}" \
  --advertise-client-urls "http://0.0.0.0:${etcd_port}" 2>&1 | tee "${VTDATAROOT}/etcd.log"

data.setup.sh:
#!/bin/bash
rmdir /docker-entrypoint-initdb.d && mkdir -p /data/mysql/auditlog && mkdir -p /data/mysql/binlog && mkdir -p /data/mysql/docker-entrypoint-initdb.d && ln -s /data/mysql/docker-entrypoint-initdb.d /docker-entrypoint-initdb.d;
exec docker-entrypoint.sh

data.reload.tpl:
{{- /* mysql global variable update */}}
{{- /* mysql using system variables reference docs: https://dev.mysql.com/doc/refman/8.0/en/using-system-variables.html */}}
{{- /*  1. system variable names must be written using underscores, not dashes. */}}
{{- /*  2. string variable 'xxx' */}}
{{- /*  3. type convert to number */}}
{{- range $pk, $pv := $.arg0 }}
	{{- $pk = trimPrefix "loose_" $pk }}
	{{- $pk = replace "-" "_" $pk }}
	{{- $var_int := -1 }}
    {{- if $pv | regexMatch "^\\d+$" }}
		{{- $var_int = atoi $pv }}
	{{- end}}
	{{- if lt $var_int 0 }}
		{{- $tmp := $pv | regexStringSubmatch "^(\\d+)K$" }}
		{{- if $tmp }}
		{{- $var_int = last $tmp | atoi | mul 1024 }}
		{{- end }}
	{{- end }}
	{{- if lt $var_int 0 }}
		{{- $tmp := $pv | regexStringSubmatch "^(\\d+)M$" }}
		{{- if $tmp }}
		{{- $var_int =  last $tmp | atoi | mul 1024 1024 }}
		{{- end }}
	{{- end }}
	{{- if lt $var_int 0 }}
		{{- $tmp := $pv | regexStringSubmatch "^(\\d+)G$" }}
		{{- if $tmp }}
		{{- $var_int = last $tmp | atoi | mul 1024 1024 1024 }}
		{{- end }}
	{{- end }}
	{{- if ge $var_int 0 }}
		{{- execSql ( printf "SET GLOBAL %s = %d" $pk $var_int ) }}
	{{- else }}
		{{- execSql ( printf "SET GLOBAL %s = '%s'" $pk $pv ) }}
	{{- end }}
{{- end }}
